import React, {useState} from "react"
import axios from "axios"

import context from "../Context"
import Header from "./Header"
import Content from "./Content"
import WeatherSearch from "./WeatherSearch"
import WeatherData from "./WeatherData"


const Main = () => {

    const [weather, setWeather] = useState()
    const api_call = async e => {
        e.preventDefault()
        const API_KEY = "f6bf1608db2876724e2c1183f3f2e287"
        const url = `https://api.openweathermap.org/data/2.5/weather?q=Warsaw&appid=${API_KEY}&units=metric`
        const request = axios.get(url)
        const response = await request
        setWeather(response.data.main)
    }

    return (
        <div className="main">
            <Header/>
            <Content>
            <WeatherSearch api_call={api_call}/>
            {weather && <WeatherData weather={weather}/>}
            </Content>
            
        </div>
    )
}

export default Main
